import {Component, OnInit} from '@angular/core';

import { products } from '../products';
import {ActivatedRoute, Routes} from "@angular/router";

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  products = products;
  category: string;
  constructor(private route: ActivatedRoute, private productService: ProductService) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.category = params['category'];
      this.getProducts();
    });
  }

  getProducts() {
    this.productService.getProducts().subscribe(products => {
      this.products = products.filter(productы => product.categoryId === this.categoryId);
    });
  }
  share(a:number) {
    window.open(`https://api.whatsapp.com/send/?phone=77073130212&text=Здравствуйте, я бы хотела приобрести этот продукт ${products[a - 1]['url']}&type=phone_number&app_absent=0`);
  }
  onNotify() {
    window.alert('You will be notified when the product goes on sale')
  }
  
}


