import { Component } from '@angular/core';
import {products} from "./products";
import {filter} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
let cosmetics = products.filter(product => product.category === 'cosmetics');
let laptops = products.filter(product => product.category === 'laptops');
let coffee_machines = products.filter(product => product.category === 'coffee machines');
let electronic_books
export class AppComponent {
  categories = [
    {
      id: 1,
      name: "Cosmetics",
      products: products.filter(product => product.category === 'cosmetics'),
    },
    {
      id: 2,
      name: "Laptops",
      products: laptops,
    },
    {
      id: 3,
      name: "Coffee machines",
    },
    {
      id: 4,
      name: "Electronic books",
    },

  ];
}


